INSERT INTO PN_PROPERTY ( CONTEXT_ID, LANGUAGE, PROPERTY_TYPE, PROPERTY, PROPERTY_VALUE, RECORD_STATUS, IS_SYSTEM_PROPERTY, IS_TRANSLATABLE_PROPERTY, PROPERTY_VALUE_CLOB ) VALUES ( 2000, 'es', 'largetext', 'prm.application.domain.directory.ldap.searchfilterexpression.instructions', '', 'A', 0, 1, 'This is a standard LDAP filter expression that will be <b><code>AND</code>ed</b> with any other filter when looking for users. If you don''t want to limit the search at all, use <code>(objectclass=*)</code>. <br>Adding a filter is recommended when there are other types of things in the LDAP server under the specified subtrees; Certain parts of the application may not behave properly if non-person objects are returned (for example, workspace invitation). <br>Examples: <code>{@prm.application.domain.directory.ldap.searchfilterexpression.example}</code>'); 
