INSERT INTO PN_PROPERTY ( CONTEXT_ID, LANGUAGE, PROPERTY_TYPE, PROPERTY, PROPERTY_VALUE, RECORD_STATUS, IS_SYSTEM_PROPERTY, IS_TRANSLATABLE_PROPERTY, PROPERTY_VALUE_CLOB ) VALUES ( 2000, 'es', 'largetext', 'prm.application.domain.directory.ldap.searchfilterexpression.instructions', '', 'A', 0, 1, 'Esta es una expresión de Filtro estándar en LDAP que será <b><code>UNIDA</code></b> con cualquier otro filtro cuando se busquen Usuarios. Si no desea limitar la búsqueda para nada, use <code>(objectclass=*)</code>. <br>Agregar un filtro es recomendado cuando hay otros tipos de cosas en el servidor LDAP bajo el Subtree especificado; ciertas partes del la aplicación tal vez no se comporten de manera adecuada si objetos que no son personas son retornados (por ejemplo: Invitaciones). <br>Por ejemplo: <code>{@prm.application.domain.directory.ldap.searchfilterexpression.example}</code>'); 
