INSERT INTO PN_PROPERTY ( CONTEXT_ID, LANGUAGE, PROPERTY_TYPE, PROPERTY, PROPERTY_VALUE, RECORD_STATUS, IS_SYSTEM_PROPERTY, IS_TRANSLATABLE_PROPERTY, PROPERTY_VALUE_CLOB ) VALUES ( 2000, 'es', 'largetext', 'prm.application.domainmigration.wizardpage1.initial.instructions', '', 'A', 0, 1, 'Este asistente causará que los {1} Usuarios Activos del Dominio "{0}" sean migrados al Dominio Objetivo a ser seleccionado por el Administrador de Aplicación. Para completar el proceso de migrar los usuarios, al Administrador de Aplicación le será requerido que: <blockquote> <li>Seleccione el Dominio Objetivo a los cuales migrar los Usuarios.</li> <li>Ingresar una explicación (que será mostrada a los Usuarios) detallando las razones del Administrador para iniciar la migración.</li> <li>Agregar el soporte para el Dominio Objetivo a cualquier configuración que actualmente no lo soporte. (Nota: Esto es solo un problema cuando el usuario es migrado a un dominio que no es soportado por la configuración con la que actualmente se loguea).</li> </blockquote> <p> <b>Nota: </b>Ejecutar este asistente no realizará efectivamente la migración de los usuarios, sino que a cada uno de los usuarios se les requerirá que migren de dominio en el próximo login. Luego de que el Administrador complete el Asistente exitosamente al usuario se le requerirá que migre al nuevo dominio. El usuario tendrá la opción de: <br> <blockquote> <li>Continuar con la migración como está especificada.</li> <li>Ser consultado para realizar la migración en 10 días.</li> <li>Cancelar la migración y permanecer como miembro del Dominio "{0}".</li> <p></blockquote> <b>Importante: </b>Los datos de usuario y su Espacio de Trabajo no serán afectados por esta Migración de Dominio.'); 
INSERT INTO PN_PROPERTY ( CONTEXT_ID, LANGUAGE, PROPERTY_TYPE, PROPERTY, PROPERTY_VALUE, RECORD_STATUS, IS_SYSTEM_PROPERTY, IS_TRANSLATABLE_PROPERTY, PROPERTY_VALUE_CLOB ) VALUES ( 2000, 'es', 'largetext', 'prm.application.domainmigration.wizardpage2.instructions', '', 'A', 0, 1, 'Seleccione el Dominio Objetivo al cual desea migrar sus Usuarios abajo. Además, ingrese instrucciones detallando las razones para iniciar la migración. Estas instrucciones aparecerán en la primer página del Asistente de Migración del Usuario.<p>'); 
